mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, status = #{status}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*,category.name as categoryName
from dish d left join  category  on d.category_id = category.id

 and d.name like concat('%',#{name},'%')
 and d.category_id = #{categoryId}
 and d.status = #{status}

order by d.create_time desc

select * from dish

    and name like concat('%',#{name},'%')
    and status = #{status}
and category_id = #{categoryId}
