mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, create_time = #{createTime},
where id = #{id}

mapper.select:
select * from setmeal
 name like concat('%',#{name},'%')
         and category_id = #{categoryId}
         and status = #{status}

order by update_time desc

select * from setmeal

     and name like concat('%',#{name},'%') 
     and category_id = #{categoryId} 
     and status = #{status}
